function IllustObject() {
  const [isHover, setHover] = useState(false);

  return (
    <>
      <Link
        className="absolute bottom-[calc(25vh+1.25rem)] right-5 top-[12.5rem] flex w-[calc(25vw)]"
        href={"/archive#I-3"}
        onMouseEnter={() => setHover(true)}
        onMouseLeave={() => {
          setHover(false);
        }}
        style={
          isHover
            ? {
                backgroundColor: "#1e1e1e",
              }
            : undefined
        }
      >
        <p className="mx-auto mt-14 text-center text-16px">
          본 전시장
          <br />
          <span className="text-12px text-grayscale-deep">
            면적 및 높이 : ♾️
          </span>
        </p>
        <p
          className="mx-6 mb-14 self-end text-16px"
          style={{ writingMode: "vertical-lr", textOrientation: "sideways" }}
        >
          2구역 : 일러스트레이션 본 전시
        </p>
        <span className="h-full w-2 border-l border-r border-grayscale-white" />
      </Link>
      <Link
        className="absolute bottom-5 right-5 h-[calc(25vh)] w-[calc(35vw)] border-b border-r"
        href={"/archive#I-3"}
        onMouseEnter={() => setHover(true)}
        onMouseLeave={() => {
          setHover(false);
        }}
        style={
          isHover
            ? {
                backgroundColor: "#1e1e1e",
              }
            : undefined
        }
      >
        <div className="mb-[0.375rem] mr-[0.375rem] flex h-[calc(100%-0.375rem)] items-end justify-center border-b border-r">
          <p className="mb-6 text-center text-16px">
            2구역 : 일러스트레이션 본 전시
          </p>
        </div>
      </Link>
    </>
  );
}

export default function Home() {
  const [_, setPopup] = usePopup();
  const [isMobile, setMobile] = useState(false);

  useEffect(() => {
    function handleResize() {
      if (window.innerWidth < 1360) {
        setMobile(true);
        return;
      } else if (window.innerHeight < 920) {
        setMobile(true);
        return;
      } else {
        setMobile(false);
        return;
      }
    }

    window.addEventListener("resize", handleResize);
    handleResize();
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    if (isMobile) {
      setPopup({ open: false, component: null });
    }
  }, [isMobile, setPopup]);

  return (
    <main className="min-h-screen border">
      {isMobile ? (
        <section className="flex min-h-[calc(100vh-2px)] flex-col">
          <div className="-ml-px -mt-px mb-auto pb-40">
            <Image
              src={require("@/assets/h-untact-lobby-object.svg")}
              alt="서울영상고등학교 영상미디어과 온라인 전시, H.Untact"
            />
          </div>
          <Link
            href="/info"
            target="_blank"
            className="flex border-t bg-grayscale-dark px-4 py-4"
          >
            <p className="w-20">0구역</p>
            <p>인포메이션 센터</p>
          </Link>
          <Link
            href="/guide.pdf"
            target="_blank"
            className="flex border-t bg-grayscale-dark px-4 py-4"
          >
            <p className="w-20">1구역</p>
            <p>데스크, 전시 안내 및 개요</p>
          </Link>
          <Link
            href="/archive#I-3"
            className="flex border-t bg-grayscale-dark px-4 py-4"
          >
            <p className="w-20">2구역</p>
            <p>일러스트레이션 본 전시</p>
          </Link>
          <Link
            href="/archive#D-3"
            className="flex border-t bg-grayscale-dark px-4 py-4"
          >
            <p className="w-20">3구역</p>
            <p>디자인 본 전시</p>
          </Link>
          <Link
            href="/archive#M-3"
            className="flex border-t bg-grayscale-dark px-4 py-4"
          >
            <p className="w-20">H구역</p>
            <p>모션그래픽 본 전시</p>
          </Link>
        </section>
      ) : (
        <>
          <Link className="absolute -mb-px -mt-px" href="/archive">
            <LobbyObject className="-mb-px -mt-px" />
          </Link>
          <button
            className="-mb-px -mt-px"
            onClick={() =>
              setPopup({
                open: true,
                component: <iframe src="/guide.pdf"></iframe>,
                title: "전시 안내 및 개요",
              })
            }
          >
            <div className="absolute right-9 top-5 flex w-[calc(100vw-650px)] flex-col pb-8 hover:bg-grayscale-dark">
              <span className="h-2 w-full border-b border-t border-grayscale-white" />
              <p className="my-6 text-center text-16px">
                1구역 : 데스크, 전시 안내 및 개요
              </p>
            </div>
          </button>
          <IllustObject />
          <Link
            href="/archive#M-3"
            className="absolute left-[calc(45vw)] top-[calc(45vh)] p-8 hover:bg-grayscale-dark"
          >
            <MotionObject />
          </Link>
          <Link
            href="/archive#D-3"
            className="absolute bottom-5 left-9 flex w-[calc(45vw)] flex-col pt-8 hover:bg-grayscale-dark"
          >
            <p className="my-6 text-center text-16px">3구역 : 디자인 본 전시</p>
            <span className="h-2 w-full border-b border-t border-grayscale-white" />
          </Link>
        </>
      )}
    </main>
  );
}
